#define _USE_MATH_DEFINES
#include <cmath>
#include <iostream>

using namespace std;

int main() {
	setlocale(LC_ALL, "rus");

	double a, b, c, D, x1, x2;

	cin >> a >> b >> c;

	D = b * b - 4 * a * c;
	if (D < 0) {
		cout << "Нет корней" << endl;
	}
	else if (D == 0) {
		x1 = (-1. * b) / (2. * a);
		cout
			<< "Сущ-ет 1 корень:" << endl
			<< "x1 = " << x1 << endl;
	}
	else if (D > 0) {
		x1 = (-1. * b + sqrt(D)) / (2. * a);
		x2 = (-1. * b - sqrt(D)) / (2. * a);
		cout
			<< "Сущ-ет 2 корня:" << endl
			<< "x1 = " << x1 << endl
			<< "x2 = " << x2 << endl;
	}

	system("pause");
}

#include <iostream>

using namespace std;

int main() {
	setlocale(LC_ALL, "rus");

	double a, b, c;
	cin >> a >> b >> c;

	if (a + b > c && a + c > b && b + c > a) {
		cout << "Треугольник существует" << endl;
		if (a == b && b == c)
			cout << "Треугольник равносторонний" << endl;
		else if (a == b || a == c || b == c)
			cout << "Треугольник равнобедренный" << endl;
		else
			cout << "Трегольник разносторонний"  << endl;
	}
	else {
		cout << "Такого треугольника нет"  << endl;
	}

	system("pause");
}

#include <iostream>

using namespace std;

int main() {
	setlocale(LC_ALL, "rus");

	double a, b;
	char act;
	cin >> a >> act >> b;

	switch (act) {
	case '+':
		cout << a + b << endl;
		break;
	case '-':
		cout << a - b << endl;
		break;
	case '*':
		cout << a * b << endl;
		break;
	case '/':
		cout << a / b << endl;
		break;
	}

	system("pause");
}

