#include <cmath>
#include <iostream>

using namespace std;

int main() {
	setlocale(LC_ALL, "rus");

	int n;
	bool isPrime = true;
	cin >> n;

	for (int i = 2; i <= sqrt(n); i++) {
		if (n % i == 0) {
			cout << "Число " << n << " - составное";
			isPrime = false;
			break;
		}
	}
	if (isPrime) {
		cout << "Число " << n << " - простое";
	}

	cout << endl;

	system("pause");
}

#include <iostream>

using namespace std;

int main() {
	setlocale(LC_ALL, "rus");

	int arr[10];

	for (int i = 0; i < 10; ++i) {
		cin >> arr[i];
	}

	int min = arr[0];
	int max = arr[0];

	for (int i = 1; i < 10; i++) {
		if (arr[i] > max) {
			max = arr[i];
		}
		if (arr[i] < min) {
			min = arr[i];
		}
	}

	cout << "Array: ";
	for (int i = 0; i < 10; ++i) {
		cout << arr[i] << "; ";
	}
	cout << endl;

	cout
		<< "Min: " << min << endl
		<< "Max: " << max << endl;

	system("pause");
}

