#include <iostream>

using namespace std;

int main() {
	setlocale(LC_ALL, "rus");

	int arr[10];

	for (int i = 0; i < 10; ++i) {
		cin >> arr[i];
	}

	long double mul = 1.;

	for (int i = 0; i < 10; ++i) {
		mul *= arr[i];
