	for (int i = 0; i < 10; ++i) {
		cout << arr[i] << "; ";
	}
	cout << endl;

	cout
		<< "Product: "        << mul << endl
		<< "Geometric mean: " << geom << endl;

	system("pause");
}

#include <iomanip>
#include <iostream>

using namespace std;

int main() {
	setlocale(LC_ALL, "rus");

	int arr[3][4];

	for (int i = 0; i < 3; ++i) {
		for (int j = 0; j < 4; ++j) {
			cin >> arr[i][j];
		}
	}

	int max = arr[0][0];
	int min = arr[0][0];

	cout << endl;
	cout << "Array:" << endl;
	for (int i = 0; i < 3; ++i) {
		for (int j = 0; j < 4; ++j) {
			if (max < arr[i][j]) {
				max = arr[i][j];
			}

			if (min > arr[i][j]) {
				min = arr[i][j];
			}

			cout << setw(4) << arr[i][j];
		}
		cout << endl;
	}
	cout << endl;

	cout
		<< "Max: " << max << endl
		<< "Min: " << min << endl;

	system("pause");
}

#include <iomanip>
#include <iostream>

using namespace std;

int main() {
	setlocale(LC_ALL, "rus");

	int arr[3][4];

	for (int i = 0; i < 3; ++i) {
		for (int j = 0; j < 4; ++j) {
			cin >> arr[i][j];
		}
	}

	int sum = 0;

	cout << endl;
	cout << "Array:" << endl;
	for (int i = 0; i < 3; ++i) {
		for (int j = 0; j < 4; ++j) {
			sum += arr[i][j];
			cout << setw(3) << arr[i][j];
		}
		cout << endl;
	}
	cout << endl;

	double avg = (double)sum / 12.;

	cout
		<< "Sum: "     << sum << endl
		<< "Average: " << avg << endl;

	system("pause");
}

#include <cmath>
#include <iomanip>
#include <iostream>

using namespace std;

int main() {
	setlocale(LC_ALL, "rus");

	int arr[3][4];

	for (int i = 0; i < 3; ++i) {
		for (int j = 0; j < 4; ++j) {
			cin >> arr[i][j];
		}
	}

	long double mul = 1.;

	cout << endl;
	cout << "Array:" << endl;
	for (int i = 0; i < 3; ++i) {
		for (int j = 0; j < 4; ++j) {
			mul *= (long double) arr[i][j];
			cout << setw(3) << arr[i][j];
		}
		cout << endl;
	}
	cout << endl;

	double geom = powl(mul, 1 / 12.);

	cout
		<< "Product: " << mul << endl
		<< "Geometric mean: " << geom << endl;

	system("pause");
